tinymce.PluginManager.add('jbimages',function(editor,url){function jbBox(){editor.windowManager.open({title:'Inserir Imagem',file:url+'/dialog-v4.htm',width:350,height:135,buttons:[{text:'Upload',classes:'widget btn primary first abs-layout-item',disabled:true,onclick:'close'},{text:'Close',onclick:'close'}]})}editor.addButton('jbimages',{tooltip:'Inserir Imagem',icon:'image',text:'Inserir',onclick:jbBox});editor.addMenuItem('jbimages',{text:'Inserir Imagem',icon:'image',context:'insert',onclick:jbBox})});